mapper.select:
SELECT ID , USER_NAME , EMAIL , STATUS , CREATE_DATE , UPDATE_DATE FROM TB_USER WHERE 1=1
AND USER_NAME = #{userName}
AND EMAIL = #{email}
AND STATUS =#{status}
ORDER BY CREATE_DATE DESC

mapper.insert:
INSERT INTO TB_USER(
    USER_NAME
    , EMAIL
    , PASSWORD
    , STATUS
    , CREATE_DATE
) VALUES (
    #{userName}
    , #{email}
    , #{password}
    , #{status}
    , NOW()
)

mapper.update:
UPDATE TB_USER
    SET USER_NAME = #{userName}
        , EMAIL = #{email}
        , PASSWORD = #{password}
        , STATUS = #{status}
        , UPDATE_DATE = NOW()
WHERE ID = #{id}
